  @action
  removePerson(int id) async {

    await Modular.get<PersonsController>().remove(id);
    Modular.to.pushReplacementNamed("/home");

  }

  @action
  FirebaseUser getUser()  {
    return Modular.get<AuthController>().user;
  }

  @action
  logoff() async{
    await Modular.get<AuthController>().logOut();
    Modular.to.pushReplacementNamed("/login");
  }



  @action
  void getFeatures(String value) {

    if (value == null || value == "")
      listFeature = principles;
    else {
      Iterable<String> iterable = principles.where((item) {
        return item.toLowerCase().contains(value.toLowerCase());
      });
      listFeature = iterable.toList();
    }
  }
  @action
  void getUndesired(String value) {

    if (value == null || value == "")
      listUndesired = principles;
    else {
      Iterable<String> iterable = principles.where((item) {
        return item.toLowerCase().contains(value.toLowerCase());
      });
      listUndesired = iterable.toList();
    }
  }
